import axios from 'axios'; // Import Axios để thực hiện yêu cầu HTTP
import './App.css'; // import một tệp CSS để tùy chỉnh giao diện
import GridLoader from "react-spinners/GridLoader"; // import GridLoader từ thư viện react-spinners để hiển thị hiệu ứng loading
import { useState, useEfect } from 'react'; // import useState và useEfect để quản lý state và thực hiện các tác vụ sau mỗi lần render.

function App() {
  const [list, setList] = useState([]); // Sử dụng hook useState để tạo ba state: list để lưu trữ danh sách đề xuất phim.
  const [isSearch, setIsSearch] = useState(false); // isSearch để kiểm tra trạng thái tìm kiếm.
  const [text,setText] = useState(""); // text để lưu trữ giá trị nhập vào ô tìm kiếm.

  // Hàm search thực hiện yêu cầu HTTP đến một API endpoint với tên phim được truyền vào.
  // Nếu có dữ liệu trả về từ server, state isSearch được đặt về false và state list được cập nhật với danh sách đề xuất từ server.
  const search = async(name) =>{
    try {
      const response = await axios.get(`http://127.0.0.1:5000/recommend/${name}`);
      if(response.data){
        setIsSearch(false);
        setList(response?.data?.recommendations);
      }
    } catch (error) {
      setIsSearch(false);
      console.log(error);
    }
  }

  // Hàm handleSearch được gọi khi nút tìm kiếm được nhấn.
  // Kiểm tra xem ô tìm kiếm có giá trị không. Nếu không có giá trị, không thực hiện tìm kiếm.
  // Nếu có giá trị, đặt trạng thái isSearch là true và gọi hàm search để thực hiện tìm kiếm.
  const handleSearch = async() =>{
    if(!text) return;
    setIsSearch(true);
    await search(text);
  }


// Render giao diện người dùng với ô tìm kiếm, nút tìm kiếm, và hiển thị kết quả hoặc hiệu ứng loading dựa trên trạng thái của ứng dụng.
  return (
    <>
      <div className='search-container'>
          <div className="header">
            <input type="text" value={text} onChange={(e)=>setText(e.target.value)} className="input-search" placeholder='Vui lòng nhập tên phim..'/>
            {
              text && (
                <button className="search-btn" onClick={handleSearch}>
              Tìm kiếm
            </button>
              )
            }
          </div>
          {
            !isSearch && list.length > 0 && (
              <div className="form">
            <h1 className="title">Ket qua ({list.length})</h1>
            <div className="list-item">
              {
                list?.map((item, index)=>(
                  <div className="item" key={index}>
                <h1>{item?.Name}</h1>
                <p>{item?.count} count</p>
              </div>
                ))
              }
            </div>
          </div>
            )
          }
          {
            isSearch && (
              <div className="form-loading">
            <GridLoader color="#FFCCCC" size={20}/>
            </div>
            )
          }
      </div>
    </>
  );
}

export default App;
